import React, { Component } from 'react';

const AuthContext = React.createContext();

class AuthProvider extends Component {
    state = {
        user : {},
    }
    setToken = (user) => {
        this.setState((prevState) => ({user}));
    }
}
export default AuthContext;
export {AuthProvider};
