import React, {useState, Component} from "react";
import {Link, Redirect} from 'react-router-dom';
import axios from 'axios';
// import logoImg from "../img/logo.jpg";
import {useAuth} from "../context/auth";
import { MDBContainer, MDBRow, MDBCol, MDBInput, MDBBtn } from 'mdbreact';


export default class Home extends Component {
    constructor(props) {
        super(props);
    }
    handleSignUp = event => {
        event.preventDefault();
        axios.post("https://localhost:4000/v1/users",  {
            email: "sang7239@gmail.edu",
            password: "password",
            passwordConf: "password",
            userName: "willy",
            firstName: "will",
            lastName: "hwang"
        }).then(result => {
            if (result.status == 200) {
                console.log(result);
                // setAuthTokens(result.data);
                // setLoggedIn(true);
            }
        }).catch(e => {
            // setIsError(true);
            console.log(e);
        })
    }
    handleLogIn = event => {
        event.preventDefault();
        axios.post("https://localhost:4000/v1/sessions",  {
            email: "sang7239@gmail.com",
            password: "password"
        }).then(result => {
            if (result.status == 200) {
                console.log(result);
                // setAuthTokens(result.data);
                // setLoggedIn(true);
            }
        }).catch(e => {
            // setIsError(true);
            console.log(e);
        })
    }
    // if (isLoggedIn) {
    //     return <Redirect to="/" />
    // }
    // return (
    //     <Card>
    //         {/* <Logo src={logoImg} /> */}
    //         <Form>
    //             <Input 
    //                 type="username" 
    //                 value = {userName}
    //                 onChange = {e => {
    //                     setUserName(e.target.value);
    //                 }}
    //                 placeholder="email"/>
    //             <Input 
    //                 type="password" 
    //                 value={password}
    //                 onChange={e => {
    //                     setPassword(e.target.value);
    //                 }}
    //                 placeholder="password"
    //                  />
    //             <Button onClick={postLogin}>Sign In</Button>
    //         </Form>
    //         <Link to="/signup">Don't have an account?</Link>
    //         {isError && <Error>The username or password provided were incorrect!</Error>}
    //     </Card>
    // )
    render(){
        return (
            <div>
                <div class="signup-form">
                <h1>Welcome to WillChat!</h1>
                <form onSubmit={this.handleSignUp} class="form-horizontal">
                    <div class="col-xs-8 col-xs-offset-4">
                        <h2>Sign Up</h2>
                    </div>		
                    <div class="form-group">
                        <label class="control-label col-xs-4">First Name</label>
                        <div class="col-xs-8">
                            <input type="text" class="form-control" name="firstname" required="required" />
                        </div>        	
                    </div>
                    <div class="form-group">
                        <label class="control-label col-xs-4">Last Name</label>
                        <div class="col-xs-8">
                            <input type="text" class="form-control" name="lastname" required="required" />
                        </div>        	
                    </div>
                    <div class="form-group">
                        <label class="control-label col-xs-4">Username</label>
                        <div class="col-xs-8">
                            <input type="text" class="form-control" name="username" required="required" />
                        </div>        	
                    </div>
                    <div class="form-group">
                        <label class="control-label col-xs-4">Email Address</label>
                        <div class="col-xs-8">
                            <input type="email" class="form-control" name="email" required="required" />
                        </div>        	
                    </div>
                    <div class="form-group">
                        <label class="control-label col-xs-4">Password</label>
                        <div class="col-xs-8">
                            <input type="password" class="form-control" name="password" required="required" />
                        </div>        	
                    </div>
                    <div class="form-group">
                        <label class="control-label col-xs-4">Confirm Password</label>
                        <div class="col-xs-8">
                            <input type="password" class="form-control" name="confirm_password" required="required" />
                        </div>        	
                    </div>
                    <div id ="signup-response" class="text-center"></div>
                    <div class="form-group">
                        <div class="col-xs-8 col-xs-offset-4">
                            <button type="submit" class="btn btn-primary btn-lg">Sign Up</button>
                        </div>  
                        <div class="col-xs-8 col-xs-offset-4">
                            <button type="button" class="btn btn-primary btn-lg" data-toggle="modal" data-target="#exampleModal" data-whatever="@mdo">Log In</button>
                        </div>  
                    </div>		      
                </form>
                <div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                    <div class="modal-dialog" role="document">
                        <form onSubmit={this.handleLogIn} class="form-horizontal">
                            <div class="col-xs-8 col-xs-offset-4">
                                <h2>Log In</h2>
                            </div>		
                            <div class="form-group">
                                <label class="control-label col-xs-4">Email Address</label>
                                <div class="col-xs-8">
                                    <input type="email" class="form-control" name="login_email" required="required" />
                                </div>        	
                            </div>
                            <div class="form-group">
                                <label class="control-label col-xs-4">Password</label>
                                <div class="col-xs-8">
                                    <input type="password" class="form-control" name="login_password" required="required" />
                                </div>        	
                            </div>
                            <div id ="login-response" class="text-center"></div>
                            <div class="form-group">
                                <div class="col-xs-8 col-xs-offset-4">
                                    <button type="submit" class="btn btn-primary btn-sm">Log In</button>
                                </div>  
                                <div class="col-xs-8 col-xs-offset-4">
                                    <button type="button" class="btn btn-primary btn-sm" data-dismiss="modal">Close</button>
                                </div>  
                            </div>	
                        </form>
                    </div>
                </div>
                </div>
            </div>
        )
    }
}
