import React, {useState, useEffect, useContext, useRef} from 'react';
import ConversationSearch from '../ConversationSearch';
import ConversationListItem from '../ConversationListItem';
import Toolbar from '../Toolbar';
import ToolbarButton from '../ToolbarButton';
import axios from 'axios';
import AuthContext from "../../context/AuthContext"
import './ConversationList.css';
import Axios from 'axios';

export default function ConversationList(props) {
  const [conversations, setConversations] = useState([]);
  const authentication = useContext(AuthContext);
  const [name, setChannelName] = useState("");
  const [description, setDescription] = useState("")
  useEffect(() => {
    getChannels()
  },[])

 const getChannels = () => {
    axios.get('https://localhost:4000/v1/channels', 
    {
      headers: {
        "Authorization": authentication.auth.token
      }
    }
    ).then(response => {
        let channels = response.data.map(results => {
          return {
            name: results.name,
            description: results.description
          }
        })
        setConversations([...conversations, ...channels])
    });
  }

  function postChannels(e) {
    e.preventDefault();
    console.log(name)
    Axios({
      method: 'POST', 
      url: "https://localhost:4000/v1/channels",
      headers: {
          Authorization: authentication.auth.token
      },
      data: {
          name: name,
          description: description,
      }
    }).then(result => {
        if (result.status == 200) {
          console.log(result)
        }
    }).catch(e => {
        console.log(e);
    })
   } 

    return (
      <div className="conversation-list">
        <Toolbar
          title="Channels"
          leftItems={[
            <ToolbarButton key="cog" icon="ion-ios-cog" />
          ]}
          rightItems={[
            // <ToolbarButton key="add" icon="ion-ios-add-circle-outline" />
            <svg xmlns="http://www.w3.org/2000/svg" width="30" height="30" viewBox="0 0 8 8"data-toggle="modal" data-target="#channelModal" data-whatever="@mdo" >
              <path d="M3 0v3h-3v2h3v3h2v-3h3v-2h-3v-3h-2z" />
            </svg>
          ]}
        />
        <ConversationSearch />
        {
          conversations.map(conversation =>
            <ConversationListItem
              key={conversation.name}
              data={conversation}
            />
          )
        }
        <div class="signup-form">
          <div class="modal fade" id="channelModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
              <div class="modal-dialog" role="document">
                  <form class="form-horizontal">
                      <div class="col-xs-8 col-xs-offset-4">
                          <h2>Create Channel</h2>
                      </div>		
                      <div class="form-group">
                          <label class="control-label col-xs-4">Channel Name</label>
                          <div class="col-xs-8">
                              <input type="text" class="form-control" name="channel_name" required="required" value ={name} onChange={e => {setChannelName(e.target.value)}}/>
                          </div>        	
                      </div>
                      <div class="form-group">
                          <label class="control-label col-xs-4">Description</label>
                          <div class="col-xs-8">
                              <input type="text" class="form-control" name="channel_description" value ={description} onChange={e => {setDescription(e.target.value)}}
                              />
                          </div>        	
                      </div>
                      <div class="form-group">
                          <label class="control-label col-xs-4" for="defaultChecked">Public</label>
                          <div class="col-xs-8">
                            <input type="radio" class="custom-control-input" id="defaultChecked" name="defaultExampleRadios" checked />
                          </div>
                      </div>
                      <div class="form-group">
                          <label class="control-label col-xs-4" for="defaultUnchecked">Private</label>
                          <div class="col-xs-8">
                            <input type="radio" class="custom-control-input" id="defaultUnchecked" name="defaultExampleRadios" />
                          </div>
                      </div>
                      <div id ="update-response" class="text-center">
                          {/* <p style={{color: this.state.color}}>{this.state.message}</p> */}
                      </div>
                      <div class="form-group">
                          <div class="col-xs-8 col-xs-offset-4">
                              <button class="btn btn-primary btn-sm" onClick={e => postChannels(e)}>Create</button>
                          </div>  
                          <div class="col-xs-8 col-xs-offset-4">
                          <button type="button" class="btn btn-primary btn-sm" data-dismiss="modal"
                          >
                              Cancel</button>
                      </div> 
                      </div>	
                  </form>
              </div>
          </div>
        </div>
      </div>
    );
}
