import React, {useState, Component, createRef, useReducer, useRef} from "react";
import {Link, Redirect} from 'react-router-dom';
import axios from 'axios';
// import logoImg from "../img/logo.jpg";
import {useAuth} from "../context/auth";
import { MDBContainer, MDBRow, MDBCol, MDBInput, MDBBtn } from 'mdbreact';


function Home() {
    /*
    type NewUser struct {
	Email        string `json:"email"`
	Password     string `json:"password"`
	PasswordConf string `json:"passwordConf"`
	UserName     string `json:"userName"`
	FirstName    string `json:"firstName"`
	LastName     string `json:"lastName"`
}
    */ 
    const email = useRef(null);
    const firstName = useRef(null);
    const lastName = useRef(null);
    const password = useRef(null);
    const passwordConf = useRef(null);
    const userName = useRef(null);
    //const { setAuthTokens } = useAuth();
    function handleSignUp() {
        console.log("sign up clicked");
        alert(password);
        axios.post("https://localhost:4000/v1/users",  {
            email: email,
            password: password,
            passwordConf: passwordConf,
            username: userName,
            firsname: firstName,
            lastname: lastName
        }).then(result => {
            if (result.status == 200) {
                console.log(result);
                // setAuthTokens(result.data);
                // setLoggedIn(true);
            }
        }).catch(e => {
            // setIsError(true);
            console.log(e);
        })
    }

    function handleLogIn() {
        console.log("hi");
    }
    // if (isLoggedIn) {
    //     return <Redirect to="/Admin" />
    // }
    return (
        <div>
            <div class="signup-form">
            <h1>Welcome to WillChat!</h1>
            <form onSubmit={handleSignUp} class="form-horizontal">
                <div class="col-xs-8 col-xs-offset-4">
                    <h2>Sign Up</h2>
                </div>		
                <div class="form-group">
                    <label class="control-label col-xs-4">First Name</label>
                    <div class="col-xs-8">
                        <input type="text" class="form-control" name="firstname" ref={firstName} required="required" />
                    </div>        	
                </div>
                <div class="form-group">
                    <label class="control-label col-xs-4">Last Name</label>
                    <div class="col-xs-8">
                        <input type="text" class="form-control" name="lastname" ref={lastName} required="required" />
                    </div>        	
                </div>
                <div class="form-group">
                    <label class="control-label col-xs-4">Username</label>
                    <div class="col-xs-8">
                        <input type="text" class="form-control" name="username" ref={userName} required="required" />
                    </div>        	
                </div>
                <div class="form-group">
                    <label class="control-label col-xs-4">Email Address</label>
                    <div class="col-xs-8">
                        <input type="email" class="form-control" name="email" ref={email} required="required" />
                    </div>        	
                </div>
                <div class="form-group">
                    <label class="control-label col-xs-4">Password</label>
                    <div class="col-xs-8">
                        <input type="password" class="form-control" name="password" ref={password} required="required" />
                    </div>        	
                </div>
                <div class="form-group">
                    <label class="control-label col-xs-4">Confirm Password</label>
                    <div class="col-xs-8">
                        <input type="password" class="form-control" name="confirm_password" ref={passwordConf} required="required" />
                    </div>        	
                </div>
                <div id ="signup-response" class="text-center"></div>
                <div class="form-group">
                    <div class="col-xs-8 col-xs-offset-4">
                        <button type="submit" class="btn btn-primary btn-lg">Sign Up</button>
                    </div>  
                    <div class="col-xs-8 col-xs-offset-4">
                        <button type="button" class="btn btn-primary btn-lg" data-toggle="modal" data-target="#exampleModal" data-whatever="@mdo">Log In</button>
                    </div>  
                </div>		      
            </form>
            <div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div class="modal-dialog" role="document">
                    <form onSubmit={handleLogIn} class="form-horizontal">
                        <div class="col-xs-8 col-xs-offset-4">
                            <h2>Log In</h2>
                        </div>		
                        <div class="form-group">
                            <label class="control-label col-xs-4">Email Address</label>
                            <div class="col-xs-8">
                            <input type="email" class="form-control" name="email" ref={email} required="required" />
                            </div>        	
                        </div>
                        <div class="form-group">
                            <label class="control-label col-xs-4">Password</label>
                            <div class="col-xs-8">
                            <input type="password" class="form-control" name="password" ref={password} required="required" />
                            </div>        	
                        </div>
                        <div id ="login-response" class="text-center"></div>
                        <div class="form-group">
                            <div class="col-xs-8 col-xs-offset-4">
                                <button type="submit" class="btn btn-primary btn-sm">Log In</button>
                            </div>  
                            <div class="col-xs-8 col-xs-offset-4">
                                <button onClick={handleLogIn}type="button" class="btn btn-primary btn-sm" data-dismiss="modal">Close</button>
                            </div>  
                        </div>	
                    </form>
                </div>
            </div>
            </div>
        </div>
    )
}
export default Home;
