import React, {useEffect, useContext, Component} from 'react';
import shave from 'shave';

import './ConversationListItem.css';
import MessageList from '../MessageList';
import ChannelContext from '../../context/ChannelContext'
import axios from 'axios';
import AuthContext from '../../context/AuthContext';

export default function ConversationListItem(props) {
  useEffect(() => {
    shave('.conversation-snippet', 20);
  })

  const { name, description, channelID } = props.data;
  const authentication = useContext(AuthContext)
  const {channelContext, setChannel} = useContext(ChannelContext)
  function displayMessages() {
    // axios.get('https://localhost:4000/v1/channels/'+ channelID, 
    // {
    //   headers: {
    //     "Authorization": authentication.auth.token
    //   }
    // }
    // ).then(response => {
    //     console.log(response.data);
    // });
    console.log(channelID);
    setChannel(channelID)
  }
  return (
    <div>
      <div className="conversation-list-item" onClick={(displayMessages)}>
        <div className="conversation-info">
          <h1 className="conversation-title">{ name }</h1>
          <p className="conversation-snippet">{ description }</p>
        </div>
      </div>
    </div>

  );
}
