import React, {useEffect, useState, useContext, Component} from 'react';
import Compose from '../Compose';
import Toolbar from '../Toolbar';
import ToolbarButton from '../ToolbarButton';
import Message from '../Message';
import moment from 'moment';
import ChannelContext from '../../context/ChannelContext'
import './MessageList.css';
import axios from 'axios';
import AuthContext from '../../context/AuthContext';

export default class MessageList extends Component {
  static contextType = AuthContext
  constructor(props) {
    super(props);
    this.state = {
      currentChannel: this.props.currentChannel,
      currentChannelName: this.props.currentChannelName,
      currentUserID: this.props.currentUserID,
      isChannelCreator: this.props.isChannelCreator,
      messages: []
    }
    this.update = this.update.bind(this);
  }
  componentDidMount() {
    const auth = this.context;
    this.scrollToBottom();
  }
  componentDidUpdate() {
    this.scrollToBottom();
  }

  scrollToBottom = () => {
    this.messagesEnd.scrollIntoView({ behavior: "smooth" });
  }

  componentWillReceiveProps(newProps) {
    const {auth} = this.context
    axios.get('https://localhost:4000/v1/channels/'+ newProps.currentChannel, 
    {
      headers: {
        "Authorization": auth.token
      }
    }
    ).then(response => {
        console.log(response.data);
        let msgs = response.data.map(results => {
          return {
            author: results.creatorID,
            message: results.body,
            timestamp: results.createdAt,
          }
        })
        this.setState({messages: msgs});
    });
    this.setState({currentChannel: newProps.currentChannel});
  }

  update =() => {
    const {auth} = this.context
    axios.get('https://localhost:4000/v1/channels/'+ this.state.currentChannel, 
    {
      headers: {
        "Authorization": auth.token
      }
    }
    ).then(response => {
        let msgs = response.data.map(results => {
          return {
            author: results.creatorID,
            message: results.body,
            timestamp: results.createdAt,
          }
        })
        this.setState({messages: msgs});
      });
  }
  renderMessages = (messages) => {
    let MY_USER_ID = this.props.currentUserID;
    let i = 0;
    let messageCount = messages.length;
    let tempMessages = [];

    while (i < messageCount) {
      let previous = messages[i - 1];
      let current = messages[i];
      let next = messages[i + 1];
      let isMine = current.author === MY_USER_ID;
      let currentMoment = moment(current.timestamp);
      let prevBySameAuthor = false;
      let nextBySameAuthor = false;
      let startsSequence = true;
      let endsSequence = true;
      let showTimestamp = true;

      if (previous) {
        let previousMoment = moment(previous.timestamp);
        let previousDuration = moment.duration(currentMoment.diff(previousMoment));
        prevBySameAuthor = previous.author === current.author;

        if (prevBySameAuthor && previousDuration.as('hours') < 1) {
          startsSequence = false;
        }

        if (previousDuration.as('hours') < 1) {
          showTimestamp = false;
        }
      }

      if (next) {
        let nextMoment = moment(next.timestamp);
        let nextDuration = moment.duration(nextMoment.diff(currentMoment));
        nextBySameAuthor = next.author === current.author;

        if (nextBySameAuthor && nextDuration.as('hours') < 1) {
          endsSequence = false;
        }
      }

      tempMessages.push(
        <Message
          key={i}
          isMine={isMine}
          startsSequence={startsSequence}
          endsSequence={endsSequence}
          showTimestamp={showTimestamp}
          data={current}
        />
      );

      // Proceed to the next message.
      i += 1;
    }
    return tempMessages;
  }
  render() {
    const channelExists = this.state.currentChannel != ""
    return(        
      <div className="message-list">
        <Toolbar
          title={this.props.isChannelCreator}
          rightItems={[
            <ToolbarButton key="info" icon="ion-ios-information-circle-outline" />,
            <ToolbarButton key="video" icon="ion-ios-videocam" />,
            <ToolbarButton key="phone" icon="ion-ios-call" />
          ]}
        />
        {channelExists 
          ? <Compose currentChannel={this.state.currentChannel} update={this.update}/>
          : <h1>Create or Join Channel</h1>
        }
        <div className="message-list-container">
          {
            this.renderMessages(this.state.messages)
          }
          </div>
          <div style={{ float:"left", clear: "both" }}
             ref={(el) => { this.messagesEnd = el; }}>
        </div>
      </div>
    );
  }
}
