import React, {useState, Component} from "react";
import {Link, Redirect} from 'react-router-dom';
import {Button} from "../components/AuthForm";
import AuthContext from "../context/AuthContext";
import Axios from "axios";

export default class Admin extends Component {
    static contextType = AuthContext
    componentDidMount() {
        const auth = this.context;
    }
    logOut = (event, auth, setAuth) => {
        event.preventDefault();
        Axios.delete("https://localhost:4000/v1/sessions/mine", {

        });
        const newauth = {token: null, loggedIn: false};
        setAuth(newauth);
    }
    render() {
        const { auth, setAuth } = this.context
        if (!auth.loggedIn) {
            return <Redirect to="/home" />
        }
        return (
            <div>
                <div>Admin Page</div>
                <div class="col-xs-8 col-xs-offset-4">
                    <button onClick={(e) => {this.logOut(e, auth, setAuth)}} class="btn btn-primary btn-lg">Sign Out</button>
                </div>
            </div>
        )
    }
}
