import React, {useEffect, useContext, Component} from 'react';
import shave from 'shave';
import Compose from '../Compose';
import Toolbar from '../Toolbar';
import ToolbarButton from '../ToolbarButton';
import Message from '../Message';
import moment from 'moment';
import './ConversationListItem.css';
import ChannelContext from '../../context/ChannelContext'
import MessageList from '../MessageList';

export default class ConversationListItem extends Component {
  static contextType = ChannelContext
  constructor(props) {
    super(props);
  }
  componentDidMount() {
    const channel = this.context;
  }

  render() {
      const {channel, setChannel} = this.context
      return (
        <div>
          <div className="conversation-list-item" onClick={() => {
              }}>
            <div className="conversation-info">
              <h1 className="conversation-title">{ this.props.data.name }</h1>
              <p className="conversation-snippet">{ this.props.data.description }</p>
            </div>
          </div>
          <div className="message-list">
          <Toolbar
            title="Conversation Title"
            rightItems={[
              <ToolbarButton key="info" icon="ion-ios-information-circle-outline" />,
              <ToolbarButton key="video" icon="ion-ios-videocam" />,
              <ToolbarButton key="phone" icon="ion-ios-call" />
            ]}
          />

          <div className="message-list-container">{renderMessages()}</div>

          <Compose rightItems={[
            <ToolbarButton key="photo" icon="ion-ios-camera" />,
            <ToolbarButton key="image" icon="ion-ios-image" />,
            <ToolbarButton key="audio" icon="ion-ios-mic" />,
            <ToolbarButton key="money" icon="ion-ios-card" />,
            <ToolbarButton key="games" icon="ion-logo-game-controller-b" />,
            <ToolbarButton key="emoji" icon="ion-ios-happy" />
          ]}/>
        </div>
        </div>

      );
  }
}
