import React, {useState, Component, createRef} from "react";
import {Link, Redirect} from 'react-router-dom';
import axios from 'axios';
// import logoImg from "../img/logo.jpg";
import {useAuth} from "../context/auth";
import { MDBContainer, MDBRow, MDBCol, MDBInput, MDBBtn } from 'mdbreact';


function Home() {
    /*
    type NewUser struct {
	Email        string `json:"email"`
	Password     string `json:"password"`
	PasswordConf string `json:"passwordConf"`
	UserName     string `json:"userName"`
	FirstName    string `json:"firstName"`
	LastName     string `json:"lastName"`
}
    */ 
    const [isLoggedIn, setLoggedIn] = useState(false);
    const [isError, setIsError] = useState(false);
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [passwordConf, setPasswordConf] = useState("");
    const [userName, setUserName] = useState("");
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");

    const { setAuthTokens } = useAuth();
    function handleSignUp() {
        axios.post("https://localhost:4000/v1/users",  {
            email: email,
            password: password,
            passwordConf: passwordConf,
            username: userName,
            firsname: firstName,
            lastname: lastName
        }).then(result => {
            if (result.status == 200) {
                console.log(result);
                // setAuthTokens(result.data);
                // setLoggedIn(true);
            }
        }).catch(e => {
            // setIsError(true);
            console.log(e);
        })
    }

    function handleLogIn() {

    }
    if (isLoggedIn) {
        return <Redirect to="/Admin" />
    }
    return (
        <div>
            <div class="signup-form">
            <h1>Welcome to WillChat!</h1>
            <form onSubmit={handleSignUp} class="form-horizontal">
                <div class="col-xs-8 col-xs-offset-4">
                    <h2>Sign Up</h2>
                </div>		
                <div class="form-group">
                    <label class="control-label col-xs-4">First Name</label>
                    <div class="col-xs-8">
                        <input type="text" class="form-control" name="firstname" value={firstName}  required="required" 
                        onChange={e => {
                            setFirstName(e.target.value);
                          }}/>
                    </div>        	
                </div>
                <div class="form-group">
                    <label class="control-label col-xs-4">Last Name</label>
                    <div class="col-xs-8">
                        <input type="text" class="form-control" name="lastname" value={lastName} required="required" />
                    </div>        	
                </div>
                <div class="form-group">
                    <label class="control-label col-xs-4">Username</label>
                    <div class="col-xs-8">
                        <input type="text" class="form-control" name="username" value={userName} required="required" />
                    </div>        	
                </div>
                <div class="form-group">
                    <label class="control-label col-xs-4">Email Address</label>
                    <div class="col-xs-8">
                        <input type="email" class="form-control" name="email" value={email} required="required" />
                    </div>        	
                </div>
                <div class="form-group">
                    <label class="control-label col-xs-4">Password</label>
                    <div class="col-xs-8">
                        <input type="password" class="form-control" name="password" value={password} required="required" />
                    </div>        	
                </div>
                <div class="form-group">
                    <label class="control-label col-xs-4">Confirm Password</label>
                    <div class="col-xs-8">
                        <input type="password" class="form-control" name="confirm_password" value={passwordConf} required="required" />
                    </div>        	
                </div>
                <div id ="signup-response" class="text-center"></div>
                <div class="form-group">
                    <div class="col-xs-8 col-xs-offset-4">
                        <button type="submit" class="btn btn-primary btn-lg">Sign Up</button>
                    </div>  
                    <div class="col-xs-8 col-xs-offset-4">
                        <button type="button" class="btn btn-primary btn-lg" data-toggle="modal" data-target="#exampleModal" data-whatever="@mdo">Log In</button>
                    </div>  
                </div>		      
            </form>
            <div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div class="modal-dialog" role="document">
                    <form onSubmit={handleLogIn} class="form-horizontal">
                        <div class="col-xs-8 col-xs-offset-4">
                            <h2>Log In</h2>
                        </div>		
                        <div class="form-group">
                            <label class="control-label col-xs-4">Email Address</label>
                            <div class="col-xs-8">
                                <input type="email" class="form-control" name="login_email" required="required" />
                            </div>        	
                        </div>
                        <div class="form-group">
                            <label class="control-label col-xs-4">Password</label>
                            <div class="col-xs-8">
                                <input type="password" class="form-control" name="login_password" required="required" />
                            </div>        	
                        </div>
                        <div id ="login-response" class="text-center"></div>
                        <div class="form-group">
                            <div class="col-xs-8 col-xs-offset-4">
                                <button type="submit" class="btn btn-primary btn-sm">Log In</button>
                            </div>  
                            <div class="col-xs-8 col-xs-offset-4">
                                <button type="button" class="btn btn-primary btn-sm" data-dismiss="modal">Close</button>
                            </div>  
                        </div>	
                    </form>
                </div>
            </div>
            </div>
        </div>
    )
}
export default Home;
