import React, {useState} from "react";
import {Link, Redirect} from 'react-router-dom';
import axios from 'axios';
// import logoImg from "../img/logo.jpg";
//import {Card, Logo, Form, Input, Button, Error} from "../components/AuthForm";
import {useAuth} from "../context/auth";

function Login(props) {
    const [isLoggedIn, setLoggedIn] = useState(false);
    const [isError, setIsError] = useState(false);
    const [userName, setUserName] = useState("");
    const [password, setPassword] = useState("");
    const {setAuthTokens} = useAuth();

    function postLogin() {
        axios.post("https://www.somePlace.com/auth/login",  {
            userName,
            password
        }).then(result => {
            if (result.status == 200) {
                setAuthTokens(result.data);
                setLoggedIn(true);
            }
        }).catch(e => {
            setIsError(true);
        })
    }
    if (isLoggedIn) {
        return <Redirect to="/login" />
    }
    return (
        // <Card>
        //     {/* <Logo src={logoImg} /> */}
        //     <Form>
        //         <Input 
        //             type="username" 
        //             value = {userName}
        //             onChange = {e => {
        //                 setUserName(e.target.value);
        //             }}
        //             placeholder="email"/>
        //         <Input 
        //             type="password" 
        //             value={password}
        //             onChange={e => {
        //                 setPassword(e.target.value);
        //             }}
        //             placeholder="password"
        //              />
        //         <Button onClick={postLogin}>Sign In</Button>
        //     </Form>
        //     <Link to="/signup">Don't have an account?</Link>
        //     {isError && <Error>The username or password provided were incorrect!</Error>}
        // </Card>
        <div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div class="modal-dialog" role="document">
                <form id="login" action="" class="form-horizontal">
                    <div class="col-xs-8 col-xs-offset-4">
                        <h2>Log In</h2>
                    </div>		
                    <div class="form-group">
                        <label class="control-label col-xs-4">Email Address</label>
                        <div class="col-xs-8">
                            <input type="email" class="form-control" name="login_email" required="required" />
                        </div>        	
                    </div>
                    <div class="form-group">
                        <label class="control-label col-xs-4">Password</label>
                        <div class="col-xs-8">
                            <input type="password" class="form-control" name="login_password" required="required" />
                        </div>        	
                    </div>
                    <div id ="login-response" class="text-center"></div>
                    <div class="form-group">
                        <div class="col-xs-8 col-xs-offset-4">
                            <button type="submit" class="btn btn-primary btn-lg">Log In</button>
                            <button type="button" class="btn btn-primary btn-lg" data-dismiss="modal">Close</button>
                        </div>  
                    </div>	
                </form>
            </div>
        </div>
    )
}
export default Login;
